import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Navbar from "./components/Navbar";
import Products from "./pages/Products";
import AddProduct from "./pages/AddProduct";
import PrivateRoute from "./components/PrivateRoute";
import EditProduct from "./pages/EditProduct";
import Profile from "./pages/Profile";
import Favorites from "./pages/Favorites";
import Cart from "./pages/Cart";
import Checkout from "./pages/Checkout";
import OrderSuccess from "./pages/OrderSuccess";
import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";


// Stripe
import { Elements } from "@stripe/react-stripe-js";
import { stripePromise } from "./stripe/stripeConfig";

function App() {
  const [clientSecret, setClientSecret] = useState(null);

  useEffect(() => {
    // Only fetch clientSecret once, for demo purposes use a minimal $1 intent
    fetch("http://localhost:5000/api/payments/create-payment-intent", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ amount: 1 }), // replace with actual total during real checkout
    })
      .then((res) => res.json())
      .then((data) => setClientSecret(data.clientSecret))
      .catch((err) => console.error("Failed to load Stripe clientSecret", err));
  }, []);

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/products" element={<Products />} />
        <Route path="/add-product" element={<PrivateRoute><AddProduct /></PrivateRoute>} />
        <Route path="/edit-product/:id" element={<EditProduct />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/favorites" element={<Favorites />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/order-success" element={<OrderSuccess />} />

    <Route
      path="/checkout"
      element={
        localStorage.getItem("user") ? (
          clientSecret ? (
            <Elements stripe={stripePromise} options={{ clientSecret }}>
              <Checkout />
            </Elements>
      ) : (
        <div className="p-8 text-center">Loading checkout...</div>
      )
    ) : (
      <Navigate to="/login" state={{ from: "/checkout" }} />
    )
  }
/>

      </Routes>
    </Router>
  );
}

export default App;
