import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const storedUser = localStorage.getItem("user");
  const [user, setUser] = useState(storedUser ? JSON.parse(storedUser) : null);
  const [form, setForm] = useState({ name: "", email: "" });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    if (user) {
      setForm({ name: user.name, email: user.email });
    }
  }, [user]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(`http://localhost:5000/api/users/${user.id}`, form);
      localStorage.setItem("user", JSON.stringify(res.data));
      setUser(res.data);
      setMsg("Profile updated!");
    } catch (err) {
      console.error("Update failed:", err);
      setMsg("Failed to update profile.");
    }
  };

  if (!user) return <p className="text-center mt-10">Please log in to view your profile.</p>;

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <form onSubmit={handleUpdate} className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4 text-blue-600">Your Profile</h2>
        <input
          name="name"
          value={form.name}
          placeholder="Name"
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded mb-4"
        />
        <input
          name="email"
          value={form.email}
          placeholder="Email"
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded mb-4"
        />
        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
          Save Changes
        </button>
        {msg && <p className="text-center text-green-600 mt-3">{msg}</p>}
      </form>
    </div>
  );
};

export default Profile;
