import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const storedUser = localStorage.getItem("user");
  const [user, setUser] = useState(storedUser ? JSON.parse(storedUser) : null);
  const [form, setForm] = useState({ name: "", email: "" });
  const [msg, setMsg] = useState("");
  const [imageUploading, setImageUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  useEffect(() => {
    if (user) {
      setForm({ name: user.name, email: user.email });
    }
  }, [user]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(`http://localhost:5000/api/users/${user.id}`, form);
      localStorage.setItem("user", JSON.stringify(res.data));
      setUser(res.data);
      setMsg("Profile updated!");
    } catch (err) {
      console.error("Update failed:", err);
      setMsg("Failed to update profile.");
    }
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("image", file);
    setImageUploading(true);

    try {
      const res = await axios.post(
        `http://localhost:5000/api/users/${user.id}/upload`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
          onUploadProgress: (progressEvent) => {
            const percent = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setUploadProgress(percent);
          },
        }
      );

      const updatedUser = { ...user, profilePicture: res.data.image };
      setUser(updatedUser);
      localStorage.setItem("user", JSON.stringify(updatedUser));
      setMsg("Profile picture updated!");
    } catch (err) {
      console.error("Upload error:", err);
      setMsg("Failed to upload profile picture.");
    } finally {
      setImageUploading(false);
      setUploadProgress(0);
    }
  };

  const handleRemoveImage = async () => {
    try {
      const res = await axios.delete(`http://localhost:5000/api/users/${user.id}/remove-picture`);
      const updatedUser = { ...user, profilePicture: "" };
      setUser(updatedUser);
      localStorage.setItem("user", JSON.stringify(updatedUser));
      setMsg("Profile picture removed.");
    } catch (err) {
      console.error("Remove error:", err);
      setMsg("Failed to remove profile picture.");
    }
  };

  if (!user) return <p className="text-center mt-10">Please log in to view your profile.</p>;

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <form onSubmit={handleUpdate} className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-blue-600 text-center">Your Profile</h2>

        {/* Profile Image */}
        <div className="flex justify-center mb-4">
          <img
            src={user.profilePicture || "https://via.placeholder.com/96?text=Avatar"}
            alt="Profile"
            className="w-24 h-24 rounded-full object-cover"
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-semibold text-gray-700 mb-1">Profile Picture</label>
          <input type="file" accept="image/*" onChange={handleImageUpload} />
          {imageUploading && (
            <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
              <div
                className="bg-blue-600 h-2 rounded-full"
                style={{ width: `${uploadProgress}%` }}
              ></div>
            </div>
          )}
          {user.profilePicture && (
            <button
              type="button"
              onClick={handleRemoveImage}
              className="mt-2 text-red-600 hover:underline text-sm"
            >
              Remove picture
            </button>
          )}
        </div>

        <div className="mb-4">
          <label className="block text-sm font-semibold text-gray-700 mb-1">Name</label>
          <input
            name="name"
            value={form.name}
            placeholder="Enter your name"
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>

        <div className="mb-6">
          <label className="block text-sm font-semibold text-gray-700 mb-1">Email</label>
          <input
            name="email"
            value={form.email}
            placeholder="Enter your email"
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700 transition duration-200"
        >
          Save Changes
        </button>

        {msg && <p className="text-center text-green-600 mt-4 font-medium">{msg}</p>}
      </form>
    </div>
  );
};

export default Profile;
