import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

import heroImage from "../assets/images/hero.jpg";
import deliveryIcon from "../assets/images/delivery.png";
import discountIcon from "../assets/images/discount.png";
import supportIcon from "../assets/images/support.png";

const Home = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [featured, setFeatured] = useState([]);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const { data } = await axios.get("/api/products");
        if (!mounted) return;
        const items = Array.isArray(data) ? data.slice(0, 8) : [];
        setFeatured(items);
      } catch {
        // graceful fallback placeholders if API isn’t ready
        setFeatured(
          Array.from({ length: 8 }).map((_, i) => ({
            _id: `ph-${i + 1}`,
            name: `Product ${i + 1}`,
            price: 29.99 + i,
            image: `https://placehold.co/600x400?text=Product+${i + 1}`,
            countInStock: 10,
          }))
        );
      } finally {
        setLoading(false);
      }
    })();
    return () => {
      mounted = false;
    };
  }, []);

  const addToCart = (p) => {
    const cart = JSON.parse(localStorage.getItem("cart") || "[]");
    const idx = cart.findIndex((i) => i._id === p._id);
    if (idx >= 0) cart[idx].quantity += 1;
    else cart.push({ ...p, quantity: 1 });
    localStorage.setItem("cart", JSON.stringify(cart));
    navigate("/cart");
  };

  const categories = ["Laptops", "Phones", "Headphones", "Gaming", "Home", "Accessories"];

  return (
    <div className="bg-gray-50">
      {/* HERO */}
      <section className="relative isolate">
        <img
          src={heroImage}
          alt=""
          className="absolute inset-0 h-full w-full object-cover"
        />
        <div
          className="absolute inset-0 bg-gradient-to-r from-indigo-700/90 to-indigo-600/90"
          aria-hidden
        />
        <div className="relative max-w-7xl mx-auto px-4 py-20 md:py-28 grid md:grid-cols-2 gap-10 items-center">
          <div className="text-white">
            <h1 className="text-4xl md:text-5xl font-extrabold leading-tight">
              Discover the Latest Products
            </h1>
            <p className="mt-3 text-white/90">
              Shop top-rated electronics, fashion, and accessories — handpicked for quality and value.
            </p>
            <div className="mt-6 flex flex-wrap gap-3">
              <Link
                to="/products"
                className="inline-flex items-center justify-center px-6 py-3 rounded-lg bg-emerald-600 hover:bg-emerald-700 font-semibold text-white"
              >
                Shop Now
              </Link>
              <Link
                to="/register"
                className="inline-flex items-center justify-center px-6 py-3 rounded-lg bg-white/10 hover:bg-white/20 font-semibold text-white"
              >
                Create Account
              </Link>
            </div>
          </div>
          <div className="hidden md:block" />
        </div>
      </section>

      {/* CATEGORY PILLS */}
      <section className="max-w-7xl mx-auto px-4 mt-6">
        <div className="flex gap-2 flex-wrap">
          {categories.map((c) => (
            <Link
              key={c}
              to={`/products?category=${encodeURIComponent(c)}`}
              className="px-4 py-2 rounded-full bg-white hover:bg-gray-50 border border-gray-200 text-sm font-medium shadow-sm"
            >
              {c}
            </Link>
          ))}
        </div>
      </section>

      {/* VALUE PROPS */}
      <section className="py-12 px-4">
        <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-6">
          {[
            { icon: deliveryIcon, title: "Fast Delivery", text: "Get your items delivered within days!" },
            { icon: discountIcon, title: "Great Deals", text: "Enjoy discounts on top brands and items." },
            { icon: supportIcon, title: "24/7 Support", text: "We're here to help, anytime you need us." },
          ].map((f) => (
            <div
              key={f.title}
              className="bg-white rounded-xl shadow-[0_1px_2px_rgba(0,0,0,0.06),0_10px_20px_rgba(0,0,0,0.06)] ring-1 ring-black/5 p-6 text-center"
            >
              <img src={f.icon} alt="" className="mx-auto mb-3 h-12 w-12 object-contain" />
              <h3 className="font-semibold text-lg">{f.title}</h3>
              <p className="text-gray-600 mt-1">{f.text}</p>
            </div>
          ))}
        </div>
      </section>

      {/* FEATURED PRODUCTS */}
      <section className="py-6 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-2xl font-bold text-gray-900">Featured Products</h2>
            <p className="text-gray-600 mt-2">Popular picks from our store</p>
          </div>

          {loading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
              {Array.from({ length: 8 }).map((_, i) => (
                <div
                  key={i}
                  className="bg-white rounded-xl shadow-[0_1px_2px_rgba(0,0,0,0.06),0_10px_20px_rgba(0,0,0,0.06)] ring-1 ring-black/5 p-4 animate-pulse"
                >
                  <div className="h-40 bg-gray-200 rounded mb-4" />
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-2" />
                  <div className="h-4 bg-gray-200 rounded w-1/3" />
                </div>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
              {featured.map((p) => (
                <div
                  key={p._id}
                  className="bg-white rounded-xl shadow-[0_1px_2px_rgba(0,0,0,0.06),0_10px_20px_rgba(0,0,0,0.06)] ring-1 ring-black/5 overflow-hidden"
                >
                  <div className="p-3">
                    <img
                      src={p.image?.url || p.image || `https://placehold.co/600x400?text=${encodeURIComponent(p.name)}`}
                      alt={p.name}
                      className="w-full h-44 object-contain bg-white rounded-lg"
                      onError={(e) => (e.currentTarget.src = "https://placehold.co/600x400?text=No+Image")}
                    />
                  </div>
                  <div className="px-4 pb-4">
                    <div className="text-sm text-gray-500">{p.countInStock > 0 ? "In stock" : "Out of stock"}</div>
                    <h3 className="font-semibold mt-1 line-clamp-1">{p.name}</h3>
                    <div className="mt-2 flex items-center justify-between">
                      <span className="text-lg font-bold">${Number(p.price).toFixed(2)}</span>
                      <button
                        onClick={() => addToCart(p)}
                        className="bg-emerald-600 hover:bg-emerald-700 text-white text-sm font-semibold px-4 py-2 rounded-lg"
                      >
                        Add to cart
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          <div className="text-center mt-8">
            <Link
              to="/products"
              className="inline-flex items-center justify-center px-5 py-3 rounded-lg bg-indigo-600 hover:bg-indigo-700 text-white font-semibold"
            >
              Browse All Products
            </Link>
          </div>
        </div>
      </section>

      {/* NEWSLETTER */}
      <section className="bg-white py-12 px-4">
        <div className="max-w-2xl mx-auto text-center">
          <h2 className="text-2xl font-bold">Stay Updated</h2>
          <p className="text-gray-600 mt-1">Get exclusive offers & updates in your inbox</p>
          <form
            className="mt-4 flex flex-col sm:flex-row items-center gap-2 justify-center"
