import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [form, setForm] = useState({ name: "", address: "" });
  const [msg, setMsg] = useState("");
  const user = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const getTotal = () =>
    cart.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name || !form.address) {
      return setMsg("Name and address are required.");
    }

    try {
      await axios.post("http://localhost:5000/api/orders", {
        userId: user.id,
        name: form.name,
        address: form.address,
        items: cart,
        total: getTotal(),
      });

      localStorage.removeItem("cart");
      setMsg("Order placed successfully!");
      setTimeout(() => navigate("/orders"), 1500);
    } catch (err) {
      console.error("Checkout error:", err);
      setMsg("Failed to place order.");
    }
  };

  if (!user) return <p className="text-center mt-10">Please log in to checkout.</p>;

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-6">Checkout</h2>

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <form onSubmit={handleSubmit} className="space-y-4 max-w-md">
          <div>
            <label className="block text-sm font-semibold">Name</label>
            <input
              name="name"
              value={form.name}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded"
            />
          </div>

          <div>
            <label className="block text-sm font-semibold">Address</label>
            <textarea
              name="address"
              value={form.address}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded"
            />
          </div>

          <div>
            <h3 className="text-lg font-semibold mt-6 mb-2">Order Summary</h3>
            {cart.map((item) => (
              <div key={item._id} className="flex justify-between">
                <span>{item.name} x {item.quantity}</span>
                <span>${(item.price * item.quantity).toFixed(2)}</span>
              </div>
            ))}
            <div className="font-bold mt-2">Total: ${getTotal()}</div>
          </div>

          <button
            type="submit"
            className="w-full bg-green-600 text-white p-2 rounded hover:bg-green-700"
          >
            Place Order
          </button>

          {msg && <p className="text-center mt-2 text-blue-600">{msg}</p>}
        </form>
      )}
    </div>
  );
};

export default Checkout;
